/**
 * add style to svg template
 * @param titleColor
 * @param descriptionColor
 * @returns
 */
function getCss(titleColor: string, descriptionColor: string) {
	return `

	@font-face {
		font-family: 'Epilogue';
		font-style:  normal;
		font-weight: 100 900;
		src: url(data:font/woff2;charset=utf-8;base64,${fonts}) format('woff2');
	}
	.title {
		font-size: 36px	;
		line-height: 1;
		font-family: Epilogue;
		font-weight: 700;
		fill: ${titleColor};
	}
	.description {
		font-size: 24px;
		line-height: 1;
		font-family: Epilogue;
		font-weight: 600;
		fill: ${descriptionColor};
	}
	`;
}

